--
-- Name: eval_template(text, character varying[]); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION eval_template(_tpl text, VARIADIC _bindings character varying[]) RETURNS text
    LANGUAGE plpgsql
    AS $$
DECLARE
result text := _tpl;
BEGIN
  FOR i IN 1..(array_upper(_bindings, 1)/2) LOOP
    result := replace(result, '{{' || _bindings[i*2 - 1] || '}}', _bindings[i*2]);
  END LOOP;
  RETURN result;
END
$$;


--
-- Name: gen_select_sql(character varying[], character varying); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION gen_select_sql(var_path character varying[], schm character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $_$
  DECLARE
  level integer;
  isArray boolean;
  columns varchar;
  selects varchar;
  subselect text;
  -- HACK! HACK! HACK!
  skip_table boolean := false;

  BEGIN
    level := array_length(var_path, 1);

    SELECT n."max" = '*'
    INTO isArray
    FROM meta.resource_elements_expanded_with_types n
    WHERE n.path = var_path;

    SELECT array_to_string(array_agg('t' || level::varchar || '."' || underscore(fhir.array_last(n.path)) || '" as "' || camelize(fhir.array_last(n.path)) || '"'), ', ')
    INTO columns
    FROM meta.resource_elements_expanded_with_types n
    JOIN meta.primitive_types pt ON underscore(pt.type) = underscore(n.type)
    WHERE fhir.array_pop(n.path) = var_path;

    if columns is not null then
      columns := columns || ', t' || level::varchar || '._index';
    else
      skip_table := true;
    end if;

    SELECT array_to_string(array_agg(E'(\n' || indent(gen_select_sql(n.path, schm), 3) || E'\n) as "' || camelize(fhir.array_last(n.path)) || '"'), E',\n')
    INTO selects
    FROM meta.resource_elements_expanded_with_types n
    LEFT JOIN meta.primitive_types pt on underscore(pt.type) = underscore(n.type)
    WHERE pt.type IS NULL AND fhir.array_pop(n.path) = var_path and fhir.array_last(n.path) not in ('contained');

    IF selects IS NULL AND columns IS NULL THEN
      RETURN 'NULL';
    ELSE
      subselect :=
         CASE WHEN level = 1 THEN '' ELSE E'\nselect ' END ||

         COALESCE(selects, '') ||
         (CASE WHEN selects IS NOT NULL AND columns IS NOT NULL THEN E',\n' ELSE '' END) ||
         COALESCE(columns, '') ||

         E'\nfrom ' ||
           '"' || schm || '"."' || fhir.table_name(var_path) || '" t' || level::varchar ||

         CASE WHEN level > 1 THEN
           E'\nwhere t' || level::varchar || '."_version_id" = t1."_version_id"'
         ELSE
           ''
         END ||
         CASE WHEN level > 2 THEN
           ' and t' || level::varchar || '."_parent_id" = t' || (level - 1)::varchar || '."_id"'
         ELSE
           ''
         END;

      IF level = 1 THEN
        RETURN $SELECT$
          SELECT t1._version_id,
                 t1.id,
                 t1.resource_type as "resourceType",
                 CASE
                     WHEN t1._container_id IS NULL THEN
                       (
                         SELECT array_to_json(array_agg(fhir.select_contained(r._version_id, fhir.table_name(ARRAY[r.resource_type])) ORDER BY r._index))
                         --SELECT array_to_json(array_agg(fhir.select_contained(r._version_id, fhir.table_name(ARRAY[r.resource_type]))))
                         FROM fhir.resource r
                         WHERE r._container_id = t1._version_id
                       )
                     ELSE NULL
                 END AS "contained",
        $SELECT$
        || subselect;

      ELSE
        RETURN
          CASE WHEN isArray THEN
            case
            when skip_table then
              'select array_to_json(array_agg(row_to_json(t_' || level::varchar || ', true)), true) from ('
            else
              'select array_to_json(array_agg(row_to_json(t_' || level::varchar || ', true) ORDER BY t_' || level::varchar || '._index), true) from ('
            end
            || indent(subselect, 1)
            || E'\n) t_' || level::varchar
          ELSE
            'select row_to_json(t_' || level::varchar || ', true) from ('
            || indent(subselect, 1)
            || E'\n) t_' || level::varchar
          END;
      END IF;
    END IF;
  END
$_$;


