--
-- Name: table_name(character varying[]); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION table_name(path character varying[]) RETURNS character varying
    LANGUAGE sql IMMUTABLE
    AS $$
  SELECT string_agg(part, '_') FROM (
    SELECT fhir.underscore(coalesce(sn.alias, pth.unnest)) as part
      FROM (SELECT *, row_number() OVER () FROM unnest(path)) pth
      LEFT JOIN fhir.short_names sn
      ON sn.name = fhir.underscore(pth.unnest)
      ORDER BY row_number
    ) _;
$$;


--
-- Name: underscore(character varying); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION underscore(str character varying) RETURNS character varying
    LANGUAGE sql IMMUTABLE
    AS $$
  SELECT
   lower(
    replace(
      regexp_replace(
        regexp_replace(str, '([a-z\d])([A-Z]+)', '\1_\2', 'g'),
        '[-\s]+', '_', 'g'),
      '.', '')); -- problem with path: {schedule,repeat} with type Schedule.repeat
$$;


--
-- Name: update_resource(uuid, json); Type: FUNCTION; Schema: fhir; Owner: -
--

CREATE FUNCTION update_resource(id uuid, resource_data json) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
  is_exists boolean;
BEGIN
  IF NOT EXISTS(select 1 FROM fhir.resource WHERE _state = 'current' and _logical_id = update_resource.id) THEN
    RAISE EXCEPTION 'Resource with id % not found', id;
  ELSE
    UPDATE fhir.resource SET _state = 'archived' WHERE _state = 'current' and _logical_id = update_resource.id;
    PERFORM fhir.insert_resource(resource_data, null::uuid, id);
  END IF;
  RETURN 0::integer;
END
$$;


