--
-- Name: view_adverse_reaction_full; Type: VIEW; Schema: fhir; Owner: -
--

CREATE VIEW view_adverse_reaction_full AS
 SELECT res_table._logical_id,
    row_to_json(t_1.*, true) AS json,
    res_table._container_id,
    res_table.id,
    t_1._version_id,
    res_table._state,
    res_table._last_modified_date
   FROM (( SELECT t1._version_id,
            t1.id,
            t1.resource_type AS "resourceType",
                CASE
                    WHEN (t1._container_id IS NULL) THEN ( SELECT array_to_json(array_agg(select_contained(r._version_id, table_name(ARRAY[r.resource_type])) ORDER BY r._index)) AS array_to_json
                       FROM resource r
                      WHERE (r._container_id = t1._version_id))
                    ELSE NULL::json
                END AS contained,
            ( SELECT array_to_json(array_agg(row_to_json(t_2.*, true) ORDER BY t_2._index), true) AS array_to_json
                   FROM ( SELECT ( SELECT row_to_json(t_3.*, true) AS row_to_json
                                   FROM ( SELECT t3.reference,
                                            t3.display,
                                            t3._index
                                           FROM adverse_reaction_exposure_substance t3
                                          WHERE ((t3._version_id = t1._version_id) AND (t3._parent_id = t2._id))) t_3) AS substance,
                            t2.type,
                            t2.causality_expectation AS "causalityExpectation",
                            t2.date,
