            ( SELECT row_to_json(t_2.*, true) AS row_to_json
                   FROM ( SELECT NULL::unknown AS "group",
                            ( SELECT row_to_json(t_3.*, true) AS row_to_json
                                   FROM ( SELECT ( SELECT array_to_json(array_agg(row_to_json(t_4.*, true) ORDER BY t_4._index), true) AS array_to_json
                                                   FROM ( SELECT ( SELECT array_to_json(array_agg(row_to_json(t_5.*, true) ORDER BY t_5._index), true) AS array_to_json
                                                                   FROM ( SELECT t5.reference,
                                                                            t5.display,
