                                                            t4.code,
                                                            t4."primary",
                                                            t4.system,
                                                            t4._index
                                                           FROM questionnaire_group_name_cd t4
                                                          WHERE ((t4._version_id = t1._version_id) AND (t4._parent_id = t3._id))) t_4) AS coding,
                                            t3.text,
                                            t3._index
                                           FROM questionnaire_group_name t3
                                          WHERE ((t3._version_id = t1._version_id) AND (t3._parent_id = t2._id))) t_3) AS name,
                            ( SELECT array_to_json(array_agg(row_to_json(t_3.*, true) ORDER BY t_3._index), true) AS array_to_json
                                   FROM ( SELECT ( SELECT array_to_json(array_agg(row_to_json(t_4.*, true) ORDER BY t_4._index), true) AS array_to_json
                                                   FROM ( SELECT ( SELECT row_to_json(t_5.*, true) AS row_to_json
                                                                   FROM ( SELECT t5.reference,
                                                                            t5.display,
                                                                            t5._index
                                                                           FROM questionnaire_group_question_choice_vs t5
                                                                          WHERE ((t5._version_id = t1._version_id) AND (t5._parent_id = t4._id))) t_5) AS "valueSet",
                                                            t4.version,
                                                            t4.display,
                                                            t4.code,
                                                            t4."primary",
                                                            t4.system,
                                                            t4._index
                                                           FROM questionnaire_group_question_choice t4
                                                          WHERE ((t4._version_id = t1._version_id) AND (t4._parent_id = t3._id))) t_4) AS choice,
                                            NULL::unknown AS "data*",
                                            NULL::unknown AS "group",
                                            ( SELECT row_to_json(t_4.*, true) AS row_to_json
                                                   FROM ( SELECT ( SELECT array_to_json(array_agg(row_to_json(t_5.*, true) ORDER BY t_5._index), true) AS array_to_json
                                                                   FROM ( SELECT ( SELECT array_to_json(array_agg(row_to_json(t_6.*, true) ORDER BY t_6._index), true) AS array_to_json
                                                                                   FROM ( SELECT t6.reference,
