                                                                                           FROM questionnaire_group_question_name_cd_vs t6
                                                                                          WHERE ((t6._version_id = t1._version_id) AND (t6._parent_id = t5._id))) t_6) AS "valueSet",
                                                                            t5.version,
                                                                            t5.display,
                                                                            t5.code,
                                                                            t5."primary",
                                                                            t5.system,
                                                                            t5._index
                                                                           FROM questionnaire_group_question_name_cd t5
                                                                          WHERE ((t5._version_id = t1._version_id) AND (t5._parent_id = t4._id))) t_5) AS coding,
                                                            t4.text,
                                                            t4._index
                                                           FROM questionnaire_group_question_name t4
                                                          WHERE ((t4._version_id = t1._version_id) AND (t4._parent_id = t3._id))) t_4) AS name,
                                            ( SELECT row_to_json(t_4.*, true) AS row_to_json
