                                          WHERE ((t3._version_id = t1._version_id) AND (t3._parent_id = t2._id))) t_3) AS subject,
                            t2.text,
                            t2.header,
                            t2._index
                           FROM questionnaire_group t2
                          WHERE (t2._version_id = t1._version_id)) t_2) AS "group",
            ( SELECT array_to_json(array_agg(row_to_json(t_2.*, true) ORDER BY t_2._index), true) AS array_to_json
                   FROM ( SELECT ( SELECT row_to_json(t_3.*, true) AS row_to_json
                                   FROM ( SELECT t3.reference,
                                            t3.display,
                                            t3._index
                                           FROM questionnaire_idn_assigner t3
                                          WHERE ((t3._version_id = t1._version_id) AND (t3._parent_id = t2._id))) t_3) AS assigner,
                            ( SELECT row_to_json(t_3.*, true) AS row_to_json
