                                                          WHERE ((t4._version_id = t1._version_id) AND (t4._parent_id = t3._id))) t_4) AS "valueSet",
                                            t3.version,
                                            t3.display,
                                            t3.code,
                                            t3."primary",
                                            t3.system,
                                            t3._index
                                           FROM questionnaire_name_cd t3
                                          WHERE ((t3._version_id = t1._version_id) AND (t3._parent_id = t2._id))) t_3) AS coding,
                            t2.text,
                            t2._index
