                          WHERE (t2._version_id = t1._version_id)) t_2) AS name,
            ( SELECT row_to_json(t_2.*, true) AS row_to_json
                   FROM ( SELECT t2.reference,
                            t2.display,
                            t2._index
                           FROM questionnaire_source t2
                          WHERE (t2._version_id = t1._version_id)) t_2) AS source,
            ( SELECT row_to_json(t_2.*, true) AS row_to_json
                   FROM ( SELECT t2.reference,
                            t2.display,
                            t2._index
                           FROM questionnaire_subject t2
                          WHERE (t2._version_id = t1._version_id)) t_2) AS subject,
            ( SELECT row_to_json(t_2.*, true) AS row_to_json
                   FROM ( SELECT t2.div,
                            t2.status,
                            t2._index
                           FROM questionnaire_text t2
                          WHERE (t2._version_id = t1._version_id)) t_2) AS text,
            t1.status,
            t1.authored,
            t1._index
           FROM questionnaire t1) t_1
   JOIN questionnaire res_table ON ((res_table._version_id = t_1._version_id)));


