      '::text, VARIADIC ARRAY['table_name'::character varying, resource_tables.table_name, 'path'::character varying, (quote_literal((resource_tables.path)::text))::character varying, 'value'::character varying, ((('((p.value::json)->'''::text || (fhir.array_last(resource_tables.path))::text) || ''')'::text))::character varying, 'parent_table'::character varying, fhir.table_name(fhir.array_pop(resource_tables.path)), 'parent_id'::character varying, (
